CREATE TABLE `fw_task_act_his` (
  `id` int(11) NOT NULL,
  `c_task` int(11) DEFAULT NULL COMMENT '名称',
  `c_act` int(11) DEFAULT NULL,
  `c_status` int(11) DEFAULT NULL COMMENT '状态',
  `c_user` int(11) DEFAULT NULL COMMENT '操作员',
  `c_time_begin` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '开始时间',
