/*Table structure for table `t_customer` */

DROP TABLE IF EXISTS `t_customer`;

CREATE TABLE `t_customer` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `c_name` varchar(255) NOT NULL COMMENT '客户名称',
  `c_phone` varchar(100) NOT NULL COMMENT '客户电话',
  `c_type` int(11) NOT NULL COMMENT '客户类型',
  `c_source` int(11) DEFAULT NULL COMMENT '400获知渠道',
  `c_address` varchar(255) DEFAULT NULL COMMENT '客户地址',
  `c_province` varchar(10) NOT NULL COMMENT '省份',
  `c_city` varchar(10) NOT NULL COMMENT '城市',
  `c_country` varchar(10) NOT NULL COMMENT '区县',
  `c_occupation` varchar(100) DEFAULT NULL COMMENT '职业',
  `c_age` int(11) DEFAULT NULL COMMENT '年龄',
  `c_education` int(11) DEFAULT NULL COMMENT '学历',
  `c_buy_type` int(11) DEFAULT NULL COMMENT '购买意向',
  `c_time_join` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '加入时间',
  `c_status` int(11) NOT NULL COMMENT '当前状态',
  `c_user` int(11) NOT NULL COMMENT '操作员',
  `c_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '更新时间',
  `c_memo` varchar(255) DEFAULT NULL COMMENT '备注',
  `c_group` int(11) DEFAULT NULL COMMENT '账套',
  PRIMARY KEY (`id`),
  KEY `idx_customer_type` (`c_type`),
  KEY `idx_customer_time` (`c_time`),
  KEY `idx_customer_area` (`c_province`,`c_city`,`c_country`)
