/* Procedure structure for procedure `p_module_copy` */

/*!50003 DROP PROCEDURE IF EXISTS  `p_module_copy` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `p_module_copy`(in module_old varchar(50))
BEGIN
    /*复制模块设置*/
	declare oldID	int;
	DECLARE newID	INT;
	
	select id into oldID from t_module where c_modulename =  module_old;
	insert into t_module(c_modulename,c_module_rec,c_datasource,c_table,c_multiselect,c_pager,c_page_size,c_sort_by,c_isquery,c_type,
		c_other,c_status,c_edit_column,c_user,c_time,c_memo,c_mui,c_path,c_alias)
          select concat(c_modulename,'_copy'),c_module_rec,c_datasource,c_table,c_multiselect,c_pager,c_page_size,c_sort_by,c_isquery,c_type,
		c_other,c_status,c_edit_column,c_user,c_time,c_memo,c_mui,c_path,c_alias
          from t_module where c_modulename =  module_old;
        set  newID = @@IDENTITY;
    
    insert t_module_col(c_module,c_column,c_coltype,c_scale,c_name,c_desc,c_format,c_order,c_width,c_style,c_isretrieve,c_isshow,c_isview,c_type,c_type_sum,
				c_user,c_time,c_memo,c_mui)
		select  newID,c_column,c_coltype,c_scale,c_name,c_desc,c_format,c_order,c_width,c_style,c_isretrieve,c_isshow,c_isview,c_type,c_type_sum,
				c_user,c_time,c_memo,c_mui
		from t_module_col where c_module= oldID order by c_order;
		
	insert t_module_query(c_module,c_column,c_coltype,c_scale,c_name,c_desc,c_order,c_isshow,c_op,c_default,c_type,c_format,c_style,c_suffix,
				c_width,c_panel_index,c_user,c_time,c_memo,c_mui)
		select  newID,c_column,c_coltype,c_scale,c_name,c_desc,c_order,c_isshow,c_op,c_default,c_type,c_format,c_style,c_suffix,
				c_width,c_panel_index,c_user,c_time,c_memo,c_mui
		from t_module_query where c_module= oldID order by c_order;
		
	insert t_module_btn(c_module,c_location,c_label,c_object,c_class,c_style,c_url,c_opentype,c_options,c_title,c_icon,c_isshow,
				c_user,c_time,c_memo,c_onclick,c_mui)
		select  newID,c_location,c_label,c_object,c_class,c_style,c_url,c_opentype,c_options,c_title,c_icon,c_isshow,
				c_user,c_time,c_memo,c_onclick,c_mui
		from t_module_btn where c_module= oldID order by c_location;
		
	insert t_module_edit(c_module,c_column,c_coltype,c_scale,c_name,c_desc,c_order,c_isshow,c_editable,c_type,c_format,c_style,c_suffix,
				c_width,c_isrequired,c_user,c_time,c_memo,c_mui)				
		select  newID,c_column,c_coltype,c_scale,c_name,c_desc,c_order,c_isshow,c_editable,c_type,c_format,c_style,c_suffix,
				c_width,c_isrequired,c_user,c_time,c_memo,c_mui
		from t_module_edit where c_module= oldID order by c_order;
		
	select  newID;
    END */$$
DELIMITER ;

