/* Procedure structure for procedure `p_user_privilege_ispower` */

/*!50003 DROP PROCEDURE IF EXISTS  `p_user_privilege_ispower` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `p_user_privilege_ispower`(
	userid integer,
	privilegeid integer,
	privilegeobj varchar(255))
BEGIN
	DECLARE priviCount int;
	if(privilegeID =0) then
		select privilegeID =id from t_code where c_object = privilegeObj;
	end if;
	
	if(exists(select * from t_user_privilege where c_user=userID)) then
		select priviCount=count(c_privilege) from t_user_privilege  
			where c_user=userID and c_privilege=privilegeID and c_deny=1;
	else
		select priviCount=count(c_privilege) from t_role_privilege  
			where c_role in(select c_role from t_role_user where c_user=userID)
				and c_privilege=privilegeID and c_deny=1;
	end if;
	select priviCount;
    END */$$
DELIMITER ;

